package br.com.tt;

public class Programa {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Variavel de texto
		//É uma classe
		String nome = "Aguirre";
		
		//Variavel do tipo inteiro
		//Variavel primitiva 
		int idade = 18;
		//Variavel primitiva, pois inicia com letra minuscula
		//Utilizada para valores variaveis
		double salario = 123.45D;		
		
		System.out.println("Iniciando o Sistema");
		System.out.println("O meu nome é " + nome);
		System.out.println("Minha idade é " + idade + " anos. ");
		System.out.println("Meu salário é R$ " + salario);
	}

}
